<?php
/**
 * An String Handler
 *
 * @author	Maiz
 * @since	2014-01-08
 */
class Str
{
	/**
	 * Cut a string to a specific length, an ellipsis will be added 
	 * to the end of this string if the length of string is not long enough
	 *
	 * @version	2014-01-08
	 *
	 * @param	string	A string
	 * @param	int		The target length
	 * @return	string	The sub-string
	 */
	static function cut($str, $len)
	{
		$str_len = self::len($str);
		if ($len >= $str_len)
		{
			return $str;
		}
		return self::sub($str, 0, $len-1).'…';
	}
	
	
	/**
	 * Flatten a string, that is, remove its HTML and line-break
	 *
	 * @version	2014-01-08
	 *
	 * @param	string	A string
	 * @return	string	Filtered result
	 */
	static function flatten($str)
	{
		$str = strip_tags($str);
		$str = str_replace(array("\n","\r","\t"), ' ', $str);
		$str = preg_replace('/ {2,}/', ' ', $str);
		return $str;
	}
	

	/**
	 * Get the lenght of a string, Non-English characters count 1 char
	 *
	 * @version	2014-01-08
	 *
	 * @param	string	A string
	 * @return	int		Length of this string
	 */
	static function len($str)
	{
		return mb_strlen($str, 'utf8');
	}
	
	
	/**
	 * Get a sub-string of a string
	 *
	 * @version	2014-01-08
	 *
	 * @param	string	A string
	 * @param	int		Start position
	 * @param	int		Length of the sub-string
	 * @return	string	The sub-string
	 */
	static function sub($str, $start = 0, $len)
	{
		return mb_substr($str, $start, $len, 'utf8');
	}
	
	
	
	/**
	 * Split a string into an array
	 *
	 * @version	2014-01-16
	 *
	 * @param	string	A string
	 * @return	array	The string array
	 */
	static public function split($str)
	{
		$arr = array();
		$len = self::len($str);
		for ($i = 0; $i < $len; $i++)
		{
			$arr[] = self::sub($str, $i, 1);
		}
		return $arr;
	}
	
	
	
	/**
	 * Convert Simplified Chinese to Traditional Chinese
	 *
	 * @version	2014-01-08
	 * @param	string	A string
	 * @return	string	The converted string
	 */
	static public function simplized($str)
	{
		$arr = self::split($str);
		foreach ($arr as $key => $value)
		{
			$k = array_search($value, self::$traditional_chars);
			if ($k !== false)
			{
				$arr[$key] = self::$simplified_chars[$k];
			}
		}
		return implode('', $arr); 
	}
	
	
	
	/**
	 * Convert Traditional Chinese to Simplified Chinese
	 *
	 * @version	2014-01-08
	 * @param	string	A string
	 * @return	string	The converted string
	 */
	static public function traditionalized($str)
	{
		$arr = self::split($str);
		foreach ($arr as $key => $value)
		{
			$k = array_search($value, self::$simplified_chars);
			if ($k !== false)
			{
				$arr[$key] = self::$traditional_chars[$k];
			}
		}
		return implode('', $arr); 
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	



